function RowSelect(row,sel){
  var rbgc = sel==1 ? "#e8e8e8" : "#f5f5f5";
  $('#'+row+' td').attr('bgColor', rbgc);
}
function select_all(){
  var frm = document.forms["results"];
  for (var i = 0; i < frm.elements.length; i++) {
    if (frm.elements[i].name.substring(0,3)=="tid"){
       frm.elements[i].checked=1;
       document.getElementById("restab").rows["a" + frm.elements[i].name.substring(3, frm.elements[i].name.length)].bgColor = "#e8e8e8";}
  }
}
function deselect_all(){
  var frm = document.forms["results"];
  for (var i = 0; i < frm.elements.length; i++) {
    if (frm.elements[i].name.substring(0,3)=="tid"){
       frm.elements[i].checked=0;
       document.getElementById("restab").rows["a" + frm.elements[i].name.substring(3, frm.elements[i].name.length)].bgColor = "#f5f5f5";}
  }
}
function reset_form(){
  var frm = document.forms["results"];
  frm.surname.value="";
  frm.countryid.value="";
  frm.town.value="";
  frm.orgid.value="";
  frm.orgname.value="";
  frm.rubriccode.value="";
  frm.metrics.value="1";
  frm.codetype.value="SPIN";
  frm.codevalue.value="";
  frm.inaffil.checked=0;
  frm.rubricinreg.checked=0;
  frm.show_nopubl.checked=0;
}
function author_search(){
  var frm = document.forms["results"];
  frm.action="authors.asp";
  frm.pagenum.value="";
  frm.target="";
  frm.submit()
}
function submit_enter(event){ 
  var keycode; 
  event = event || window.event; 
  keycode = event.keyCode || event.which;
  if(keycode==13){
    author_search();
	return false;
  }
} 
function authors_search(){
  var frm = document.forms["results"];
  frm.authors_all.value="";
  for (var i=0; i<frm.elements.length; i++){
   if(frm.elements[i].id.substring(0,3)=="tid"){
    if(frm.elements[i].checked){
    frm.authors_all.value = frm.authors_all.value + document.getElementById(frm.elements[i].name.replace("tid","hid")).value + "|";}}}
  if (frm.authors_all.value=="") {
    alerts ("Не выделено ни одного автора!")}
  else {
  frm.action="querybox.asp";
  frm.target="";
  frm.submit()}
}
function authorbox_add(){
  var frm = document.forms["results"];
  var issel=0;
  for (var i = 0; i < frm.elements.length; i++) {
    if (frm.elements[i].name.substring(0,3)=="tid"){
       if (frm.elements[i].checked) {issel=1; break}
    }
  }
  if (issel==0) {
    alerts ("Не выделено ни одного автора!")}
  else {
    anewwin=window.open("", "dialog", "resizable=0, scrollbars=0, statusbar=0, status=0, toolbar=0, menubar=0, directories=0, location=0, height=200, width=400, top=200, left=200");
    anewwin.resizeTo(400,200);
    frm.action="authorbox_author_add.asp";
    frm.target="dialog";
    frm.submit()
  }
}
function authorbox_new(aid,anam){
  var frm = document.forms["results"];
  var el = document.createElement("OPTION");
  el.text=anam;
  el.value=aid;
  frm.authorboxid.options[frm.authorboxid.options.length]=el;
  frm.authorboxid[frm.authorboxid.length-1].selected=true;
}
function goto_page(pnum){
  var frm = document.forms["results"];
  frm.pagenum.value=pnum;
  frm.action="authors.asp";
  frm.target="";
  frm.submit()
}
function show_help(topic){
  anewwin=window.open("", "addwin", "resizable=yes,scrollbars=yes,height=300,width=500,top=0,left=0");
  document.forms["results"].action=topic;
  document.forms["results"].target="addwin";
  document.forms["results"].submit()
}
