var selRow;
var row_color;
function onRowClick(row){
  if(row != selRow){
     if (selRow != null){
        selRow.bgColor = row_color;
     }
  selRow = row;
  row_color = row.bgColor;
  selRow.bgColor="#e8e8e8";
  }
}
function hideShow(elem,parent) {
  var child =  document.getElementById(elem);
  child.className = child.className == "collapsed" ? "expanded" : "collapsed";
  if (parent.className == "win_expandable") parent.src = child.className == "collapsed" ? w_down.src : w_up.src;
}
function hideShowSel(elem) {
  var child =  document.getElementById(elem);
  child.className = child.className == "collapsed" ? "expanded" : "collapsed";
  var imgparent =  document.getElementById('img_'+elem);
  if (imgparent.className == "win_expandable") imgparent.src = child.className == "collapsed" ? ws_down.src : ws_up.src;
  var txtparent =  document.getElementById('txt_'+elem);
  if (txtparent.className == "win_expandable") txtparent.innerHTML = child.className == "collapsed" ? "Раскрыть" : "Скрыть";
}
function hideShowSel1(elem) {
  var child =  document.getElementById(elem);
  $('#'+elem).slideToggle(500, function(){didScroll = true;})
  child.className = child.className == "collapsed1" ? "expanded1" : "collapsed1";
  if (child.className == "collapsed1") {
    $('#'+elem).slideUp(500, function(){didScroll = true;})}
  else {
    $('#'+elem).slideDown(500, function(){didScroll = true;})}
  var txtparent =  document.getElementById('txt_'+elem);
  if (txtparent.className == "win_expandable") txtparent.innerHTML = child.className == "collapsed1" ? "▼" : "▲";
}
function RowSelect(row,sel){
  var rbgc = sel==1 ? "#e8e8e8" : "#f5f5f5";
  $('#'+row+' td').attr('bgColor', rbgc);
}
function select_all(){
  $('input:checkbox[name^="tid"]').attr('checked', true);
  $('input:checkbox[name^="pid"]').attr('checked', true);
  $('tr[id^="arw"] td').attr('bgColor', "#e8e8e8");
  $('tr[id^="brw"] td').attr('bgColor', "#e8e8e8");
}
function deselect_all(){
  $('input:checkbox[name^="tid"]').attr('checked', false);
  $('input:checkbox[name^="pid"]').attr('checked', false);
  $('tr[id^="arw"] td').attr('bgColor', "#f5f5f5");
  $('tr[id^="brw"] td').attr('bgColor', "#f5f5f5");
}
function select_options(sname){
  $('input:checkbox[name^="'+sname+'s_"]').attr('checked', true);
  $('tr[name^="'+sname+'_"]').attr('bgColor', "#dddddd");
  checked_count(sname);
  isnew=1;
  }
function deselect_options(sname){
  $('input:checkbox[name^="'+sname+'s_"]').attr('checked', false);
  $('tr[name^="'+sname+'_"]').attr('bgColor', "#f5f5f5");
  checked_count(sname);
  isnew=1;
}
function wait_load_options(sname){
  $('span[name="'+sname+'sheader"]').text('загружается...');
}
function checked_count(sname){
  var nb=$('input:checkbox[name^="'+sname+'s_"]:checked').length;
  if (nb>0){$('span[name="'+sname+'sheader"]').html('(выделено: ' + nb + ')')}
  else{$('span[name="'+sname+'sheader"]').text('')}
}
var ir=0;
function resizelist(sname, offset) {
 if (ir <= offset+10) {
  ir=ir+1;
  $('div[id="'+sname+'_options"]').height(ir+50);
  window.setTimeout('resizelist("'+sname+'", '+offset+')', 1);
 }
}
