function reset_form(){
  var frm = document.forms["results"];
  deselect_options("rubric"); 
  deselect_options("title"); 
  deselect_options("org"); 
  deselect_options("author"); 
  deselect_options("year"); 
  deselect_options("type"); 
  deselect_options("role"); 
  deselect_options("keyword"); 
}
function pub_search(){
  var frm = document.forms["results"];
    frm.pagenum.value=1;
    frm.action="author_items.asp";
    frm.target="";
    frm.submit()
}

function itembox_add(){
  var frm = document.forms["results"];
  var issel=0;
  frm.items_all.value="";
  for (var i = 0; i < frm.elements.length; i++) {
    if (frm.elements[i].name.substring(0,3)=="tid"){
       if (frm.elements[i].checked) {
	     issel=issel+1;
	     frm.items_all.value = frm.items_all.value + frm.elements[i].name.substring(3, frm.elements[i].name.length) + "|";
	   }
    }
 }
 if (issel==0) {
    alerts ("Не выделено ни одной публикации")}
 else {
    $.fancybox({
      href : 'itembox_item_add2.asp?itemboxid='+frm.itemboxid.value+'&items_all='+frm.items_all.value ,
      title : '',
	  padding : 20,     
	  width : 480,
	  height : 150,
	  overlayOpacity : 0.3,	
	  overlayColor : '#000',
	  speedIn : 500,
	  hideOnOverlayClick : false,
	  enableEscapeButton : true,
	  type : 'iframe'
    });
  }
}

function itembox_new(aid,anam){
  var frm = document.forms["results"];
  var el = document.createElement("OPTION");
  el.text=anam;
  el.value=aid;
  frm.itemboxid.options[frm.itemboxid.options.length]=el;
  frm.itemboxid[frm.itemboxid.length-1].selected=true;
}
function itembox_add_all(){
  var frm = document.forms["results"];
    $.fancybox({
      href : 'itembox_item_add2.asp?itemboxid='+frm.itemboxid.value+'&add_all=2' ,
      title : '',
	  padding : 20,     
	  width : 480,
	  height : 150,
	  overlayOpacity : 0.3,	
	  overlayColor : '#000',
	  speedIn : 500,
	  hideOnOverlayClick : false,
	  enableEscapeButton : true,
	  type : 'iframe'
    });
}
