function order_item(id, pr){

  var frm = document.forms["results"];
  if (0-pr>=0){
    ret = false;
    confirms("На Вашем счету сейчас 0 руб. \nПри открытии доступа к полному тексту данной публикации с Вашего счета будет списано " + eval(pr) + " руб.\nПодтверждаете приобретение публикации?", function(){
    if (ret) {
      anewwin=window.open("", "fulltext", "resizable=yes,scrollbars=yes,top=0,left=0");
      frm.action="buy_item.asp";
      frm.payitemid.value=id;
      frm.target="fulltext";
      frm.submit()
	  }
    })
  }
  else {
    ret = false;
    confirms("На Вашем счету сейчас 0 руб. \nДля получения доступа к полному тексту этой публикации Вам необходимо пополнить счет как минимум на " + eval(pr-0) + " руб.\nХотите пополнить счет прямо сейчас?", function(){
    if (ret) {
      frm.paysum.value=pr-0;
      frm.payitemid.value=id;
      frm.action="account_add.asp";
      frm.target="";
      frm.submit()
	  }
    })
  }

}
